/*
 file load method as helper and model it call with controller
 crate date : 22-08-2016
 author: Piyapan Nansathid
*/

module.exports = function(method, name_file){

  /*
    get file config becose check core sys
  */
  var config = require('../../config/system/config.json');
  var load = config['load'];
  var helper = 'helper';
  var model = 'model';
    if(method === helper){
      if(load[helper] ){
        return require('../../helper/'+name_file+'.'+helper+'.js');
      }else {
        console.log("Error helper config  file system open");
      }

    }else if (method === model) {
      /*
        หลังเครื่องหมาย ?  จะเป็นเงื่อนไข
        ถ้าเป็น true จะโยน file model เข้าตัวแปลชื่อ module
        ถ้าเป็น false จะโยน console.log เข้าตัวแปลชื่อ module
        แล้ว return module กลับไป
      */
      var module = load[model] ? require('../../app/models/'+name_file+"."+model+".js"):console.log("Error model config  file system open");
      return module;
    }
    else {
      console.log('Error method require file load');
    }
}
